import React, { useState } from "react";

function List(props) {
  const [Content, setContent] = useState("");
  const [ContentList, setContentList] = useState([]);

  const onSubmit = () => {
    let TempArr = [...ContentList];
    TempArr.push(Content);
    setContentList([...TempArr]);
    setContent("");
  };
  return (
    <div>
      {ContentList.map((content, idx) => {
        return (
          <div
            key={idx}
            style={{
              width: "100%",
              marginLeft: "1rem",
            }}
          >
            내용: {content}
            <hr />
          </div>
        );
      })}
    </div>
  );
}
export default List;
